<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use App\Http\Requests;// use App\Orders;use App\klanten;use App\Leveranciers;use App\Http\Controllers\Controller;use Validator;// use App\Http\Controllers\DB;// use Illuminate\Support\Facades\DB;class LeveranciersController extends Controller{    /**     * Create a new controller instance.     *     * @return void     */    public function __construct(\App\Leveranciers\Repository $repo)    {        $this->middleware('auth');        $this->leveranciers = $repo;    }    /**     * Display a listing of the resource.     *     * @return \Illuminate\Http\Response     */    public function index()    {        return $this->leveranciers->getIndex();     }        /**     * Store a newly created resource in storage.     *     * @param  \Illuminate\Http\Request  $request     * @return \Illuminate\Http\Response     */    public function store(Request $request)    {        return $this->leveranciers->postStore($request);    }    /**     * Display the specified resource.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function show($id)    {        return $this->leveranciers->getShow($id);         }    /**     * Show the form for editing the specified resource.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function edit($id)    {        return $this->leveranciers->getEdit($id);    }    /**     * Update the specified resource in storage.     *     * @param  \Illuminate\Http\Request  $request     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function update(Request $request, $id)    {        return $this->leveranciers->getUpdate($request, $id);             }    /**     * Remove the specified resource from storage.     *     * @param  int  $id     * @return \Illuminate\Http\Response     */    public function destroy($id)    {           return $this->leveranciers->getDestroy($id);                }}